            plt.xlabel("POPULATION VECTOR ID")
            if measure == "normalized_ratio":
                plt.ylim(-1, 1)
                plt.ylabel("PRE_POST RATIO")
            elif measure == "log_like_ratio":
                plt.ylabel("LOG-LIKELIHOOD RATIO")
                y_min, y_max = ax.get_ylim()
