                ax1 = fig.add_subplot(gs[:, :6])
                ax2 = fig.add_subplot(gs[:, 8:])
                ax1.scatter(cl_features_noise[:, 0], cl_features_noise[:, 1], color="grey", label="noise")

                # compute mean waveform per hour
                cl_features_window = cell_features[:, :]
